import classes from './Cart.module.css';
import Modal from '../UI/Modal';
import React, {useContext, useState} from 'react';
import CartContext from '../../store/cart-context'
import CartItem from './CartItem';
import Checkout from './Checkout'

const Cart = props => {

    const [isCheckout, setisCheckout] = useState(false)
    const [isSubmitting, setIsSubmitting] = useState(false)
    const [didSubmitting, setDidSubmitting] = useState(false)
    const cartCtx = useContext(CartContext);

    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`
    const hasItems = cartCtx.items.length > 0;

    const cartItemRemoveHandler = id => {
        cartCtx.removeItem(id);
    };

    const cartItemHandler = item => {
        cartCtx.addItem({...item, amount:1});
    }; 

    const orderHandler = () => {
        setisCheckout(true)
    }

    const submitOrderHandler = async (userData) => {
        setIsSubmitting(true)
        await fetch('https://react-food-app-62fba-default-rtdb.europe-west1.firebasedatabase.app/orders.json', { 
            method: 'POST',
            body: JSON.stringify({ 
                user: userData,
                orderedItems: cartCtx.items
            })
        })
        setIsSubmitting(false)
        setDidSubmitting(true)
    }

    const cartItems = <ul className={classes['cart-items']}>
        {cartCtx.items.map((item) => (
        <CartItem 
        key={item.id} 
        name={item.name} 
        amount={item.amount} 
        price={item.price} 
        onRemove={cartItemRemoveHandler.bind(null, item.id)}
        onAdd={cartItemHandler.bind(null, item)}
        />
        ))}
    </ul>

    const modalActions = <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
        {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}

        </div>

            const cartModalContent = 
            <React.Fragment>
                {cartItems} 
                <div className={classes.total}>
                    <span>Total Amount</span>
                    <span>{totalAmount}</span>
                </div>
                {isCheckout && <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose} />}
                {!isCheckout && modalActions}
            </React.Fragment>
    return (
    <Modal onClose={props.onClose}>
        

    </Modal>
    )
};

export default Cart;
