import { Fragment} from 'react';
import ReactDOM from 'react-dom';
import classes from './Modal.module.css';

const Backdrop = props => {
    return <div className={classes.backdrop} onClick={props.onClose}/>
};

const ModlaOverlay = props => {
    return <div className={classes.modal}>
        <div className={classes.contet}>{props.children}</div>
    </div>
};

const portalElement = document.getElementById('overlays');

const Modal = (props) => {
    return  <Fragment>
        {ReactDOM.createPortal(<Backdrop onClose={props.children}/>, portalElement)}
        {ReactDOM.createPortal(<ModlaOverlay>{props.children}</ModlaOverlay>,
        portalElement
        )}
    </Fragment>
};

export default Modal;
